import styled from 'styled-components';


export const Pequeno = styled.div`
     
    display: flex;
    align-items: center;
    border: 1px solid black;
    padding: 20px 10px;
    margin-bottom: 10px;
    height: 100px;


img {
    width: 70px;
    margin-right: 10px;
    border-radius: 50%;
}
h4 {
    margin-right: 15px;
}
div {
    display: flex;    
    justify-items: flex-start;
}

`
